import React from "react";
import "./voter.css";
const Voter = (props) => {
  return (
    <>
      <div className="voter_card_container">
          <div className="voter_card_item">{props.voter.voterAddress}</div>
          <div className="voter_card_item">{props.voter.name}</div>
          <div className="voter_card_item">{props.voter.votingCardNumber}</div>
          <div className="voter_card_item">{ props.voter.hasVerified ? (<p>verified</p>):(<button>Verify</button>)}</div>
      </div>
    </>
  );
};

export default Voter;
