import React, { Component } from "react";
import Voting from "./contracts/Voting.json";
import getWeb3 from "./getWeb3";
import "./App.css";
import "bootstrap/dist/css/bootstrap.css";
import Admin from './Admin';
  class App extends Component {
  // state = { storageValue: 0, web3: null, accounts: null, contract: null };

  constructor(props) {
    super(props);
    this.state = {
      VotingInstance: undefined,
      account: null,
      web3: null,
      isOwner: false,

    };
   }

  componentDidMount = async () => {
    // FOR REFRESHING PAGE ONLY ONCE -
    if (!window.location.hash) {
      window.location = window.location + "#loaded";
      window.location.reload();
    }

    try {
      // Get network provider and web3 instance.
      const web3 = await getWeb3();
      // Use web3 to get the user's accounts.
      const accounts = await web3.eth.getAccounts();
      console.log("Acooutn s " + accounts[0]);

      // Get the contract instance.
      const networkId = await web3.eth.net.getId();
      const deployedNetwork = Voting.networks[networkId];
      console.log(
        "networkid: " +
          networkId +
          "  deploynetworkid : " +
          deployedNetwork.address
      );
      const instance = new web3.eth.Contract(
        Voting.abi,
        deployedNetwork && deployedNetwork.address
      );
      // Set web3, accounts, and contract to the state, and then proceed with an
      // example of interacting with the contract's methods.
      // this.sue end falseetState({ web3, accounts, contract: instance }, this.runExample);
      this.setState({
        VotingInstance: instance,
        account: accounts[0],
        web3: web3,
      });
      const owner = await this.state.VotingInstance.methods.getOwner().call();
      if (this.state.account === owner) {
        this.setState({ isOwner: true });
      }
      // this.state.VotingInstance.methods.startElection().send({from:this.state.account});

      let st = await this.state.VotingInstance.methods.getStart().call();
      let en = await this.state.VotingInstance.methods.getEnd().call();
      this.setState({ start: st, end: en});

    } catch (error) {
      // Catch any errors for any of the above operations.
      alert(
        `Failed to load web3, accounts, or contract. Check console for details.`
      );
      console.error(error);
    }
  };

  StartEndElection=(event)=>{  
     console.log(this.state.VotingInstance);
     if(event.target.name ==="startElection"){
    this.state.VotingInstance.methods.startElection().send({from: this.state.account}).then(()=>{
      console.log(this.state.start);
      this.setState({start:true, end:false});
    }

    )}
    else if(event.target.name ==="endElection"){
      this.state.VotingInstance.methods.endElection().send({from: this.state.account}).then(()=>{
        this.setState({start:false, end:true});
      }

      )}

  }


  render() {
    if (!this.state.web3) {
      return <div>Loading Web3, accounts, and contract...</div>;
    }

    return (
      <React.Fragment>
       <div className="App">

        <h1>Good to Go!</h1>

        <div>your user address is {this.state.account}</div>
        {this.state.isOwner ? (
          <div>

          you are owner!
          <Admin startStatus={this.state.start} endStatus={this.state.end} onEleClick={this.StartEndElection}/>
        </div>
        ) : (
          <div>
          you are Not owner!

           </div>

        )}
      </div>

      </React.Fragment>
    );
  }

}

export default App;
