import React, { useState } from "react";

const AddCandidateFormComp = (props) => {
  const [candidate, setcandidate] = useState({});
  const updateInfo = (event) => {
    event.persist();

    setcandidate((prevVal) => {
      return { [event.target.name]: [event.target.value], ...prevVal };
    });
  };
  const addCandidate= ()=>{
    props.addCandidate(candidate);

  }

  return (
    <>
      <div>
        <h1>{candidate.name}</h1>
        <form onSubmit={addCandidate}>
          <input
            type="text"
            name="name"
            id="name"
            value={candidate.name}
            placeholder="Name"
            onChange={updateInfo}
          />
          <br />
          <input
            type="text"
            name="party"
            id="party"
            placeholder="Party"
            onChange={updateInfo}
          />
          <br />
          <input
            type="text"
            name="manifesto"
            id="manifesto"
            placeholder="Manifesto"
            onChange={updateInfo}
          />
          <br />
          <input
            type="text"
            name="constituency"
            id="constituency"
            placeholder="Constituency"
            onChange={updateInfo}
          />
          <br />
          <input
            name="addcandidate"
            id="addcandidate"
            type="submit"
             disabled={props.stfl === true}
          >
            Add Candidate
          </input>
        </form>
      </div>
    </>
  );
};
export default AddCandidateFormComp;
