import React, { useState } from "react";

const AddCandidateFormComp = (props) => {
  const [candidate, setcandidate] = useState({});
  const updateInfo = (event) => {
   // event.persist();
    const n = event.target.name;
    const v = event.target.value;
    setcandidate((prevVal) => {
      //return { [event.target.name]: [event.target.value], ...prevVal };

    });
  };
  const addCandidate= ()=>{
    props.addCandidate(candidate);

  }

  return (
    <>
      <div>
        <h1>{candidate.name}</h1>
        <form>
          <input
            type="text"
            name="name"
            id="name"

            placeholder="Name"
            onChange={updateInfo}
          />
          <br />
          <input
            type="text"
            name="party"
            id="party"
            placeholder="Party"
            onChange={updateInfo}
          />
          <br />
          <input
            type="text"
            name="manifesto"
            id="manifesto"
            placeholder="Manifesto"
            onChange={updateInfo}
          />
          <br />
          <input
            type="text"
            name="constituency"
            id="constituency"
            placeholder="Constituency"
            onChange={updateInfo}
          />
          <br />
          <button
            name="addcandidate"
            id="addcandidate"
            onClick={addCandidate}
            disabled={props.stfl === true}
          >
            Add Candidate
          </button>
        </form>
      </div>
    </>
  );
};
export default AddCandidateFormComp;
