import React, { useState } from "react";

const AddCandidateFormComp = (props) => {
  const [candidate, setcandidate] = useState({name:"",party:"",manifesto:"",constituency:""});

  const updateInfo =  (event)=>{
    const {name,value}= event.target;
    console.log(name + " " + value);
    setcandidate(

      (prevValue)=>{
        console.log(prevValue);
        return {
          [name]:value,
          ...prevValue
        }

      }


    );

  }
const addCandidate=()=>{
  props.addCandidate(candidate);
}



  return (
    <>
      <div>
        <h1>{candidate.name}</h1>
        <form>
          <input
            type="text"
            name="name"
            id="name"

            placeholder="Name"
            onChange={updateInfo}
          />
          <br />
          <input
            type="text"
            name="party"
            id="party"
            placeholder="Party"
            onChange={updateInfo}
          />
          <br />
          <input
            type="text"
            name="manifesto"
            id="manifesto"
            placeholder="Manifesto"
            onChange={updateInfo}
          />
          <br />
          <input
            type="text"
            name="constituency"
            id="constituency"
            placeholder="Constituency"
            onChange={updateInfo}
          />
          <br />
          <button
            name="addcandidate"
            id="addcandidate"
           onClick={addCandidate}
            disabled={props.stfl === true}
          >
            Add Candidate
          </button>
        </form>
      </div>
    </>
  );
};
export default AddCandidateFormComp;
