import React, { useState } from "react";
import AddCandidate from "./AddCandidate";
import {
  Nav,
  NavLink,
  Bars,
  NavMenu,
  NavBtn,
  NavBtnLink,
} from './NavbarElements';
const Admin = (props) => {
  let status;
  let stfl=true,endfl=false;
  if (props.startStatus === true && props.endStatus === false) {
    status = <h1>"Election has been Started!"</h1>;
    stfl=false;endfl=true;
  } else if (props.startStatus === false && props.endStatus === true) {
    status = <h1>"Election has been ended!"</h1>;
    endfl=false;
  }
  return (
    <>
    <Nav>
      <Bars>
        <NavMenu>
          <NavLink to="/AddCandidate" activeStyle>Add Candidate</NavLink>
        </NavMenu>
      </Bars>
    </Nav>
      <div style={{ background: "red" }}>{status}</div>
      <button onClick={props.onEleClick} name="startElection" disabled={stfl===false}>
        Start Election
      </button>
      <button onClick={props.onEleClick} name="endElection" disabled={endfl===false}>
        End Election
      </button>
      <div>
        <form  >
          <input type="text" name="name" id="name" placeholder="Name" /><br/>
          <input type="text" name="party" id="party" placeholder="Party" /><br/>
          <input
            type="text"
            name="manifesto"
            id="manifesto"
            placeholder="Manifesto"
          /><br/>
          <input
            type="text"
            name="constituency"
            id="constituency"
            placeholder="Constituency"
          /><br/>
          <button name="addcandidate" id="addcandidate" disabled={stfl===false}>Add Candidate</button>
        </form>
        <br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/>
        <AddCandidate/>
      </div>
    </>
  );
};
export default Admin;
