import React, { useEffect, useState } from "react";

const AddCandidateFormComp = (props) => {
  const [candidate, setcandidate] = useState({name:"",party:"",manifesto:"",constituency:""});
  const[candidateList , setcandidateList ] = useState(props.candidateList);

     const updateInfo = (event) => {
       event.persist();
      setcandidate((prevProps) => ({
        ...prevProps,
        [event.target.name]: event.target.value
      }));
    };

const addCandidate=(event)=>{
  event.preventDefault();
  console.log(candidate);
  props.addCandidate(candidate);
  setcandidate({name:"",party:"",manifesto:"",constituency:""});
}



  return (
    <>
      <div>
        <h1>{candidateList.get(0)}  [[[</h1>
        <form  onSubmit={addCandidate}>
          <input
            type="text"
            name="name"
            id="name"
             value={candidate.name}
            placeholder="Name"
            onChange={updateInfo}
            required
          />
          <br />
          <input
            type="text"
            name="party"
            id="party"
            value={candidate.party}
            placeholder="Party"
            onChange={updateInfo}
            required
          />
          <br />
          <input
            type="text"
            name="manifesto"
            id="manifesto"
            value={candidate.manifesto}
            placeholder="Manifesto"
            onChange={updateInfo}
            required
          />
          <br />
          <input
            type="text"
            name="constituency"
            id="constituency"
            value={candidate.constituency}
            placeholder="Constituency"
            onChange={updateInfo}
            required
          />
          <br />
          <button
          type="submit"
            name="addcandidate"
            id="addcandidate"
            disabled={props.stfl === true}>
            Add Candidate
          </button>
        </form>



      </div>



    </>
  );
};
export default AddCandidateFormComp;
