import React, { useState } from "react";

const Admin = (props) => {
  const [electionStatus, setElectionStatus] = useState("");
  if (props.startStatus === true && props.endStatus === false) {
    setElectionStatus("Election has been Started!");
  } else if (props.startStatus === false && props.endStatus === false) {
    setElectionStatus("Election has Not Started!");
  } else {
    setElectionStatus("Election has been Ended!");
  }
  return (
    <>
      {/* <input type="text" name="name" id="name" placeholder="Name"/>
            <input type="text" name="votingCardNumber" id="votingCardNumber"placeholder="Voting Card Number"/>
            <input type="int" name="constituency" id="constituency" placeholder="Constituency"/> */}
      <div>{electionStatus}</div>
      <button>Start Election</button>
      <button> End Election</button>
    </>
  );
};
export default Admin;
