import React, { useState } from "react";

const AddCandidateFormComp = (props) => {
  const [candidate, setcandidate] = useState({name:"",party:"",manifesto:"",constituency:""});

     const updateInfo = (event) => {
       event.persist();
      setcandidate((prevProps) => ({
        ...prevProps,
        [event.target.name]: event.target.value
      }));
    };

const addCandidate=(event)=>{
  event.preventDefault();
  console.log(candidate);
  //props.addCandidate(candidate);
}



  return (
    <>
      <div>
        <h1>{candidate.name}</h1>
        <form  onSubmit={addCandidate}>
          <input
            type="text"
            name="name"
            id="name"
             value={candidate.name}
            placeholder="Name"
            onChange={updateInfo}
          />
          <br />
          <input
            type="text"
            name="party"
            id="party"
            value={candidate.party}
            placeholder="Party"
            onChange={updateInfo}
          />
          <br />
          <input
            type="text"
            name="manifesto"
            id="manifesto"
            value={candidate.manifesto}
            placeholder="Manifesto"
            onChange={updateInfo}
          />
          <br />
          <input
            type="text"
            name="constituency"
            id="constituency"
            value={candidate.manifesto}
            placeholder="Constituency"
            onChange={updateInfo}
          />
          <br />
          <button
          type="submit"
            name="addcandidate"
            id="addcandidate"

            disabled={props.stfl === true}
          >
            Add Candidate
          </button>
        </form>
      </div>
    </>
  );
};
export default AddCandidateFormComp;
