            console.log("networkid: " +networkId +"  deploynetworkid : " +deployedNetwork.address);
            const instace = new web3.eth.Contract(Voting.abi, deployedNetwork && deployedNetwork.address);
            setvotingInstance(instace);
            setaccount(accounts[0]);
            setweb3(web3);

        }
