import React, { useState,useEffect} from "react";
import AddCandidate from "./AddCandidate";
import {  Route, Link, NavLink } from "react-router-dom";
import Election from "./Election";
import VerifyVoters from "./VerifyVoters";
const Admin = (props) => {
  let ElectionStatus="---------";
  let stfl=true,endfl=false;
  if (props.startStatus === true && props.endStatus === false) {
    ElectionStatus = <h1>"Election has been Started!"</h1>;
    stfl=false;endfl=true;
  } else if (props.startStatus === false && props.endStatus === true) {
    ElectionStatus = <h1>"Election has been ended!"</h1>;
    endfl=false;
  }

  return (
    <>
      <div>

      <NavLink to="/Election">Election</NavLink>
        <NavLink to="/AddCandidate">Add Candidate</NavLink>
        <NavLink to="/VerifyVoters">Verify Voters</NavLink>
      </div>
       <switch>
        <Route exact path="/AddCandidate" ><AddCandidate stfl={props.startStatus}/></Route>  
      <Route exact path="/Election" >  <div style={{ background: "red" }}>{ElectionStatus}</div>
      <button onClick={props.onEleStartClick} name="startElection" disabled={props.startStatus === true  }>
        Start Election
      </button>
      <button onClick={props.onEleEndClick} name="endElection" disabled={props.startStatus === false || props.endStatus === true }>
        End Election 
      </button>
      {console.log(" Election "+stfl + " " + endfl)}
    </Route>
      <Route exact path="/VerifyVoters"> <VerifyVoters/> </Route>
      </switch>
    </>
  );
};
export default Admin;
