const Voters = (props) => {
    const [voter,setvoter] = useState({name:"",votingCardNumber:"",constituency:"" });
    const Updatevals=(event)=>{
            event.persist();
        setvoter((prev)=>{
            return {
                ...prev,
                [event.target.name]:event.target.value
            }

        });
        console.log(voter);

    }
    const addVoter = (event)=>{
        event.preventDefault();
  console.log(voter);
  props.requestVoter(voter);
  setvoter({name:"",votingCardNumber:"",constituency:""});

    }

  return (
    <>
      <div>
        you are Not owner!
        <h1>

          {props.candidateList.map((curcad) => {
            return (
              <h5>
                {curcad.candidateId} {curcad.name} {curcad.party}
                {curcad.manifesto} {curcad.constituency}
              </h5>
            );
          })}
        </h1>
        {
        <form  onSubmit={addVoter}>
        <input type="text" name ="name" id="name" placeholder="Name" onChange={Updatevals}/><br/>
        <input type="text" name="votingCardNumber" id="votingCardNumber" placeholder="Voting Card Number" onChange={Updatevals}/><br/>
        <input type="text" name="constituency" id="constituency" placeholder="Constituency"onChange={Updatevals}/><br/>
        <button type="submit" name="addvoter" id="addvoter" >Request Voter</button></form>
        }
     </div>
    </>
  );
};
export default Voters;
