import React from 'react';
import Voter from "./Voter"
import "./voter.css";
const VerifyVoters = (props)=>{
    return (<>
        <div className="voter_card_container">
          <div className="voter_card_item"></div>
          <div className="voter_card_item" style={{textAlign:"center"}}>{props.voter.name}</div>
          <div className="voter_card_item" style={{textAlign:"center"}}>{props.voter.votingCardNumber}</div>
          <div className="voter_card_item" style={{textAlign:"center"}}>{ props.voter.hasVerified ? (<p>verified</p>):(<button>Verify</button>)}</div>
      </div>
        {props.voterList.map((voter)=>{
            return <Voter voter={voter}/>
        })}

       </>);
}
export default  VerifyVoters;
