import React, { Component } from "react";
import Voting from "./contracts/Voting.json";
import getWeb3 from "./getWeb3";
import "./App.css";
import "bootstrap/dist/css/bootstrap.css";
import Admin from "./Admin";
import Voters from "./Voters";

class App extends Component {
  // state = { storageValue: 0, web3: null, accounts: null, contract: null };

  constructor(props) {
    super(props);
    this.state = {
      VotingInstance: undefined,
      account: null,
      web3: null,
      isOwner: false,
      candidateList: [],
      voterList:[],
       voterequested:false,
       voted:false
    };
  }

  componentDidMount = async () => {
    // FOR REFRESHING PAGE ONLY ONCE -
    if (!window.location.hash) {
      window.location = window.location + "#loaded";
      window.location.reload();
    }

    try {
      // Get network provider and web3 instance.
      const web3 = await getWeb3();
      // Use web3 to get the user's accounts.
      const accounts = await web3.eth.getAccounts();
      console.log("Acooutn s " + accounts[0]);

      // Get the contract instance.
      const networkId = await web3.eth.net.getId();
      const deployedNetwork = Voting.networks[networkId];
      console.log(
        "networkid: " +
          networkId +
          "  deploynetworkid : " +
          deployedNetwork.address
      );
      const instance = new web3.eth.Contract(
        Voting.abi,
        deployedNetwork && deployedNetwork.address
      );
      // Set web3, accounts, and contract to the state, and then proceed with an
      // example of interacting with the contract's methods.
      // this.sue end falseetState({ web3, accounts, contract: instance }, this.runExample);
      this.setState({
        VotingInstance: instance,
        account: accounts[0],
        web3: web3,
      });
      const owner = await this.state.VotingInstance.methods.getOwner().call();
      if (this.state.account === owner) {
        this.setState({ isOwner: true });
      }
      // this.state.VotingInstance.methods.startElection().send({from:this.state.account});

      let st = await this.state.VotingInstance.methods.getStart().call();
      let en = await this.state.VotingInstance.methods.getEnd().call();
      this.setState({ start: st, end: en });
      this.updateCandidateList();
      this.updateVoterList();


    } catch (error) {
      // Catch any errors for any of the above operations.
      alert(
        `Failed to load web3, accounts, or contract. Check console for details.`
      );
      console.error(error);
    }
  };
  //-----------------------------------------------------------------------------------------------------------------------

  //-----------------------------------------------------------------------------------------------------------------------
  StartElection = (event) => {
    console.log(this.state.VotingInstance);
    if (event.target.name === "startElection") {
      this.state.VotingInstance.methods
        .startElection()
        .send({ from: this.state.account })
        .then(() => {
          this.setState({ start: true, end: false });
          console.log("Election Started!!");
        });
    }
  };
  EndElection = (event) => {
    if (event.target.name === "endElection") {
      this.state.VotingInstance.methods
        .endElection()
        .send({ from: this.state.account })
        .then(() => {
          this.setState({ start: false, end: true });
          console.log("Election Ended!!");
        });
    }
  };
  updateCandidateList = () => {
    this.state.VotingInstance.methods
      .getCandidateList()
      .call()
      .then((candidates) => {
        this.setState({ candidateList: candidates });
      });
  };
  AddCandidate = (candidate) => {
    this.state.VotingInstance.methods
      .addCandidate(
        candidate.name,
        candidate.party,
        candidate.manifesto,
        candidate.constituency
      )
      .send({ from: this.state.account })
      .then((response) => {
        alert(response);
        console.log(response);
      });

    this.updateCandidateList();

    //  const t =this.state.VotingInstance.methods
    //     .getCandidateNumber()
    //     .call()
    //     .then((candidateCount)=>{

    //       console.log("----------")
    //       console.log(candidateCount);
    //       return candidateCount
    //       });
  };
  requestVoter = (voter) => {
    console.log("App js ma ");
    console.log(voter);
    this.state.VotingInstance.methods
      .requestVoter(voter.name,voter.votingCardNumber,voter.constituency)
      .send({ from: this.state.account })
      .then(()=>{
        console.log("Voter Requested")
      });
  };
  updateVoterList=()=>{
    var voterCount;
    this.
    this.state.VotingInstance.methods.getVoterList().call().then(
      (voters)=>{

        this.setState({voterList : voters});
         var i=0;
        for(i=0;i<this.state.voterList.length ;i++)
        {
          if(this.state.voterList[i].voterAddress === this.state.account)
          { 
            this.setState({voterequested:true});
            console.log(this.state.voterList);
            console.log("-----------------");
            break;
          }
        }

      }
    );

    //  this.state.VotingInstance.methods.getVoterList().call().then(
    //   (voterList)=>{
    //     console.log(voterList);
    //      var i=0;
    // for(i=0;i<voterList.length ; i++)
    // {
    //   console.log(voterList[i]);
    //   this.state.VotingInstance.methods.getvoter(voterList[i]).call().then(
    //     (cvoter)=>{console.log(cvoter);}
    //   );
    // }
    //   }
    // );

    //this.state.VotingInstance.methods.getvoter
  }
  VerifyVoter = (_address)=>{
    this.state.VotingInstance.methods.verifyVoter(_address).send({from:this.state.account}).then(
     ()=>{ alert(_address + "Verfied!!");}
    );
  }
  Vote = (candidate)=>{
    alert(candidate.candidateId);
    this.state.VotingInstance.methods.Vote(candidate.candidateId).send({from:this.state.account}).then(
      ()=>{alert("You Have Voter to"+ candidate.name)}
    );
  }

  DeclareResult = ()=>{
    alert("Result Declared!!")
  }

  render() {
    if (!this.state.web3) {
      return <div>Loading Web3, accounts, and contract...</div>;
    }

    return (
      <React.Fragment>
        <div className="App">
          {this.state.isOwner ? (
            <div>
              <Admin
                accountAddress={this.state.account}
                startStatus={this.state.start}
                endStatus={this.state.end}
                onEleStartClick={this.StartElection}
                onEleEndClick={this.EndElection}
                addCandidate={(candidate) => {
                  this.AddCandidate(candidate);
                }}
                candidateList={this.state.candidateList}
                voterList = { this.state.voterList}
                verifyVoter={(address)=>{this.VerifyVoter(address)}}
                onDeclareResutClick={this.DeclareResult}
              />
            </div>
          ) : (
            <div>
              <Voters
                voterRequested={this.state.voterequested}
                candidateList={this.state.candidateList}
                requestVoter={(voter) => {
                  this.requestVoter(voter);
                }}
                Vote={(candidate)=>{this.Vote(candidate)}}
              />
            </div>
          )}
        </div>
      </React.Fragment>
    );
  }
}

export default App;
