import React, { useState } from "react";
import AddCandidate from "./AddCandidate";
import {  Route, Link, NavLink } from "react-router-dom";
import Election from "./Election";
import VerifyVoters from "./VerifyVoters";
const Admin = (props) => {
  let ElectionStatus;
  let stfl=true,endfl=false;
  if (props.startStatus === true && props.endStatus === false) {
    ElectionStatus = <h1>"Election has been Started!"</h1>;
    stfl=false;endfl=true;
  } else if (props.startStatus === false && props.endStatus === true) {
    ElectionStatus = <h1>"Election has been ended!"</h1>;
    endfl=false;
  }
  return (
    <>
      <div>

      <NavLink to="">Election</NavLink>
        <NavLink to="/AddCandidate">Add Candidate</NavLink>
        <NavLink to="/VerifyVoters">Verify Voters</NavLink>
      </div>
       <switch>
        <Route exact path="/AddCandidate" ><AddCandidate stfl={stfl}/></Route>
      {/* <Route ><Election /></Route> */}
      <Route exact path="/" component={() => (<Election to='/home' property={props}/>)} />
      <Route exact path="/VerifyVoters"> <VerifyVoters/> </Route>
      </switch>
    </>
  );
};
export default Admin;
