import React, { useEffect }  from "react";
import AddCandidateFormComp from "./AddCandidateFormComp";
import { Route,  NavLink, Switch } from "react-router-dom";
 import VerifyVoters from "./VerifyVoters";


const Admin = (props) => {
  let ElectionStatus = "---------";

  if (props.startStatus === true && props.endStatus === false) {
    ElectionStatus = <h1>"Election has been Started!"</h1>;

  } else if (props.startStatus === false && props.endStatus === true) {
    ElectionStatus = <h1>"Election has been ended!"</h1>;

  }
  const [candidateList, setCandidateList ]= useState(props.candidateList);

  return (
    <>
      <div>
        <NavLink to="/Election">Election</NavLink>
        <NavLink to="/AddCandidate">Add Candidate</NavLink>
        <NavLink to="/VerifyVoters">Verify Voters</NavLink>
      </div>
      <Switch>
        <Route exact path="/AddCandidate">
          <AddCandidateFormComp stfl={props.startStatus}  addCandidate={  (candidate)=>{ props.addCandidate(candidate)}} candidateList ={props.candidateList}/>
        </Route>
        <Route exact path="/Election">

          <div style={{ background: "red" }}>{ElectionStatus}</div>
          <button
            onClick={props.onEleStartClick}
            name="startElection"
            disabled={props.startStatus === true}
          >
            Start Election
          </button>
          <button
            onClick={props.onEleEndClick}
            name="endElection"
            disabled={props.startStatus === false || props.endStatus === true}
          >
            End Election
          </button>
          {console.log(" Election " + props.startStatus  + " " + props.endStatus )}
        </Route>
        <Route exact path="/VerifyVoters">

          <VerifyVoters /> 
        </Route>
      </Switch>
    </>
  );
};
export default Admin;
