import React from "react";
import AddCandidateFormComp from "./AddCandidateFormComp";
import { Route, NavLink, Switch, Link } from "react-router-dom";
import VerifyVoters from "./VerifyVoters";
import styled from "styled-components";
import "./admin.css";

const Admin = (props) => {
  let ElectionStatus = "---------";
  const addressMenu = () => {
    return (
      <ul>
        <li>{props.ccountAddress}</li>
      </ul>
    );
  };
  if (props.startStatus === true && props.endStatus === false) {
    ElectionStatus = <h1>"Election has been Started!"</h1>;
  } else if (props.startStatus === false && props.endStatus === true) {
    ElectionStatus = <h1>"Election has been ended!"</h1>;
  }

  return (
    <>
      <div className="navbar">
        <p className="heading">Voting </p>
        <div className="links"  >
          <NavLink
            className="nav-item"
            to="/Election"
            style={{ textDecoration: "none" }}
            activeStyle={{ color: "blue" }}
          >
            Election
          </NavLink>
          <NavLink
            className="nav-item"
            to="/AddCandidate"
            style={{ textDecoration: "none" }}
            activeStyle={{ color: "blue" }}
          >
            Add Candidate
          </NavLink>
          <NavLink
            className="nav-item"
            to="/VerifyVoters"
            style={{ textDecoration: "none" }}
            activeStyle={{ color: "blue" }}
          >
            Verify Voters
          </NavLink>
        </div>
        <div>
          <div
            className="menu"
            style={{ textDecoration: "none" }}
            activeStyle={{ color: "blue" }}
            onClick
          >
            Admin
          </div>
        </div>
      </div>

      <Switch>
        <Route exact path="/AddCandidate">
          <AddCandidateFormComp
            stfl={props.startStatus}
            addCandidate={(candidate) => {
              props.addCandidate(candidate);
            }}
            candidateList={props.candidateList}
          />
        </Route>
        <Route exact path="/Election">
          <div style={{ background: "red" }}>{ElectionStatus}</div>
          <button
            onClick={props.onEleStartClick}
            name="startElection"
            disabled={props.startStatus === true}
          >
            Start Election
          </button>
          <button
            onClick={props.onEleEndClick}
            name="endElection"
            disabled={props.startStatus === false || props.endStatus === true}
          >
            End Election
          </button>
          {console.log(
            " Election " + props.startStatus + " " + props.endStatus
          )}
        </Route>
        <Route exact path="/VerifyVoters">
          <VerifyVoters voterList={props.voterList} />
        </Route>
      </Switch>
    </>
  );
};
export default Admin;
