import React, { useState } from "react";

const AddCandidateFormComp = (props) => {
  const [candidte, setcandidte] = useState({});
  const updateInfo = (event) => {
    event.persist();

    setcandidte((prevVal) => {
      return { [event.target.name]: [event.target.value], ...prevVal };
    });
  };

  return (
    <>
      <div>
        <h1>{candidte.name}</h1>{" "}
        <form>
          <input
            type="text"
            name="name"
            id="name"
            placeholder="Name"
            onChange={updateInfo}
          />
          <br />
          <input
            type="text"
            name="party"
            id="party"
            placeholder="Party"
            onChange={updateInfo}
          />
          <br />
          <input
            type="text"
            name="manifesto"
            id="manifesto"
            placeholder="Manifesto"
            onChange={updateInfo}
          />
          <br />
          <input
            type="text"
            name="constituency"
            id="constituency"
            placeholder="Constituency"
            onChange={updateInfo}
          />
          <br />
          <button
            name="addcandidate"
            id="addcandidate"
            disabled={props.stfl === true}
          >
            Add Candidate
          </button>
        </form>
      </div>
    </>
  );
};
export default AddCandidateFormComp;
