import React, { useState } from "react";

const Admin = (props) => {
  let  status;
   if (props.startStatus === true && props.endStatus === false) {
     status = <h1>"Election has been Started!"</h1>;
  } else if (props.startStatus === false && props.endStatus === true) {
     status = <h1>"Election has been ended!"</h1>;
   }
   return (
    <>
      {/* */}
      <div style={{background:"red"}}>{status}</div>
      <button onClick={props.onEleClick} name="startElection">Start Election</button>
      <button onClick={props.onEleClick} name="endElection"> End Election</button>
    </>
  );
};
export default Admin;
