import React, {   useState } from "react";

const AddCandidateFormComp = (props) => {
  const [candidate, setcandidate] = useState({name:"",party:"",manifesto:"",constituency:""});

     const updateInfo = (event) => {
       event.persist();
      setcandidate((prevProps) => ({
        ...prevProps,
        [event.target.name]: event.target.value
      }));
    };

const addCandidate=(event)=>{
  event.preventDefault();
  console.log(candidate);
  props.addCandidate(candidate);
  setcandidate({name:"",party:"",manifesto:"",constituency:""});
}


  return (
    <>
      <div>
        <h1> {props.candidateList.map((curcad)=>{
          return <h5>{curcad.candidateId} {curcad.name} {curcad.party} {curcad.manifesto} {curcad.constituency} </h5>
        })}</h1>
        <form  onSubmit={addCandidate}>
          <input
            type="text"
            name="name"
            id="name"
             value={candidate.name}
            placeholder="Name"
            onChange={updateInfo}
            required
          />
          <br />
          <input
            type="text"
            name="party"
            id="party"
            value={candidate.party}
            placeholder="Party"
            onChange={updateInfo}
            required
          />
          <br />
          <input
            type="text"
            name="manifesto"
            id="manifesto"
            value={candidate.manifesto}
            placeholder="Manifesto"
            onChange={updateInfo}
            required
          />
          <br />
          <input
            type="text"
            name="constituency"
            id="constituency"
            value={candidate.constituency}
            placeholder="Constituency"
            onChange={updateInfo}
            required
          />
          <br />
          <button
          type="submit"
            name="addcandidate"
            id="addcandidate"
            disabled={props.stfl === true}>
            Add Candidate
          </button>
        </form>
        <div>
          <image></image>
          <div>Name</div>
        </div>


      </div>



    </>
  );
};
export default AddCandidateFormComp;
