import React, { useState } from "react";
import CandidateCard from "./CandidateCard";
import "./candidatecard.css";
import ipfs from "./ipfs";
const AddCandidateFormComp = (props) => {
  const [candidate, setcandidate] = useState({
    name: "",
    party: "",
    manifesto: "",
    constituency: "",
    imgHash: ""
  });

  const updateInfo = (event) => {
    event.persist();
    if(event.target.name=="imgHash"){
      const file = event.target.files[0];

      let reader = new window.FileReader();
      reader.readAsArrayBuffer(file);
      reader.onload = () =>{


        const imgBuffer =  Buffer.from(reader.result);

        //console.log(imgBuffer);
        console.log( imgBuffer);
       ipfs.files.add( imgBuffer, (error, result) => {
        if(error) {
          console.error(error)
          return
        }
        var imghash =  result[0].hash ;
        console.log(candidate);
        console.log(imghash);
        setcandidate((prevProps) => ({
          ...prevProps,
          [event.target.name]: result[0].hash,
        }));

        console.log(candidate.imgHash);
        console.log(candidate)

      })
    }
    }
    else{
    setcandidate((prevProps) => ({
      ...prevProps,
      [event.target.name]: event.target.value,
    }));
  }
  };

  const addCandidate = (event) => {
    event.preventDefault();
    //console.log(candidate.imgBuffer);
    props.addCandidate(candidate);
    setcandidate({ name: "", party: "", manifesto: "", constituency: "",imgHash: "" });
  };
  const captureFile = (event) => {
    event.stopPropagation();
    event.preventDefault();



}


 // console.log(props.candidateList);

  return (
    <>
      <div>
        <div className="candidate_container">
          <h2>Candidates</h2>
          <div className="candidate_card_container">
            {props.candidateList.map((curcad) => {
              return <CandidateCard candidate={curcad} />;
            })}
          </div>
        </div>
        <h2>Add Candidate</h2>
        <form onSubmit={addCandidate}>
          <input
            type="text"
            name="name"
            id="name"
            value={candidate.name}
            placeholder="Name"
            onChange={updateInfo}
            required
          />
          <br />
          <input
            type="text"
            name="party"
            id="party"
            value={candidate.party}
            placeholder="Party"
            onChange={updateInfo}
            required
          />
          <br />
          <input
            type="text"
            name="manifesto"
            id="manifesto"
            value={candidate.manifesto}
            placeholder="Manifesto"
            onChange={updateInfo}
            required
          />
          <br />
          <input
            type="text"
            name="constituency"
            id="constituency"
            value={candidate.constituency}
            placeholder="Constituency"
            onChange={updateInfo}
            required
          />
          <br />
          Image:
          <input
            type="file"
            name="imgHash"
            value={candidate.imgHash}
            onChange={updateInfo}
            placeholder="Candidate Image"
          />
          <br />
          <button
            type="submit"
            name="addcandidate"
            id="addcandidate"
            disabled={props.stfl === true}
          >
            Add Candidate
          </button>
        </form>
      </div>
    </>
  );
};
export default AddCandidateFormComp;
