import React, { useState } from "react";
import CandidateCard from "./CandidateCard";
import "./candidatecard.css";
const Voters = (props) => {
  const [voter, setvoter] = useState({
    name: "",
    votingCardNumber: "",
    constituency: "",
  });
  const Updatevals = (event) => {
    event.persist();
    setvoter((prev) => {
      return {
        ...prev,
        [event.target.name]: event.target.value,
      };
    });
    console.log(voter);
  };
  const addVoter = (event) => {
    event.preventDefault();
    console.log(voter);
    props.requestVoter(voter);
    setvoter({ name: "", votingCardNumber: "", constituency: "" });
  };

  return (
    <>
      <div>
        <div className="candidate_container">
          <h2>Candidates</h2>
          <div className="candidate_card_container">
            {props.candidateList.map((curcad) => {
              return (
                <CandidateCard
                  candidate={curcad}
                  voterFlag={true}
                  startStatus={props.start}
                  endStatus={props.end}
                  resultDeclared={props.resultDeclared}
                  voterVerified={props.voterVerified}
                  onClick={(candidate) => {
                    props.Vote(candidate);
                  }}
                />
              );
            })}
          </div>
          <div>
            {props.resultDeclared ? (
              <h3>{props.candidateList[1].voteCount}</h3>
            ) : (
              <></>
            )}
          </div>
        </div>
        {props.voterRequested ? (
          <h1></h1>
        ) : (
          <form onSubmit={addVoter}>
            <input
              type="text"
              name="name"
              id="name"
              placeholder="Name"
              onChange={Updatevals}
            />
            <br />
            <input
              type="text"
              name="votingCardNumber"
              id="votingCardNumber"
              placeholder="Voting Card Number"
              onChange={Updatevals}
            />
            <br />
            <input
              type="text"
              name="constituency"
              id="constituency"
              placeholder="Constituency"
              onChange={Updatevals}
            />
            <br />
            <button
              type="submit"
              name="addvoter"
              id="addvoter"
              disabled={props.resultDeclared === true}
            >
              Request Voter
            </button>
          </form>
        )}
      </div>
    </>
  );
};
export default Voters;
