const AddCandidate=(props)=>
{
  const [candidte, setcandidte] = useState({});
 const  updateInfo = (event) =>{
  const na = event.target.name ;
  const val = event.target.value;
  setcandidte( (prevVal)=>{

return {    na:val , 
  ...prevVal
}

  });


 }

return<>
    <div> <form  >

          <input type="text" name="name" id="name" placeholder="Name" onChange={updateInfo} /><br/>
          <input type="text" name="party" id="party" placeholder="Party"  onChange={updateInfo}/><br/>
          <input
            type="text"
            name="manifesto"
            id="manifesto"
            placeholder="Manifesto"
            onChange={updateInfo}
          /><br/>
          <input
            type="text"
            name="constituency"
            id="constituency"
            placeholder="Constituency"
            onChange={updateInfo}
          /><br/>
          <button name="addcandidate" id="addcandidate" disabled={props.stfl===true}>Add Candidate</button>
        </form></div>
</>
}
export default AddCandidate;
